						["Initialization"] = "function (modTable)\n    \n    modTable.data ={}\n    \n    modTable.spellId = 215568\n    modTable.spellName = GetSpellInfo(modTable.spellId)\n    modTable.texture = GetSpellTexture(modTable.spellId)\n    \n    local function OnEvent(self, event)\n        if event == \"PLAYER_ENTERING_WORLD\" then \n            wipe(modTable.data) \n            return\n        end\n        \n        local details = { CombatLogGetCurrentEventInfo() }\n        if details[2] ~= \"SPELL_CAST_SUCCESS\" then return end\n        if details[5] ~= UnitName(\"player\") then return end\n        if details[12] ~= 23881 then return end\n        local guid = details[8]\n        modTable.data[guid] = true\n    end\n    \n    modTable.eventFrame = modTable.eventFrame or GlobalPlaterFreshMeatEventFrame\n    if not modTable.eventFrame then\n        modTable.eventFrame = CreateFrame(\"Frame\", \"GlobalPlaterFreshMeatEventFrame\")\n        modTable.eventFrame:RegisterEvent(\"COMBAT_LOG_EVENT_UNFILTERED\")\n        modTable.eventFrame:RegisterEvent(\"PLAYER_ENTERING_WORLD\")\n    end\n    \n    modTable.eventFrame:SetScript(\"OnEvent\",  OnEvent)\n    \nend",
						["Nameplate Updated"] = "function (self, unitId, unitFrame, envTable, modTable)\n    \n    if unitFrame.ActorType ~= \"enemynpc\" then return end\n    \n    local used = modTable.data[unitFrame.namePlateUnitGUID]\n    if used then return end\n    \n    Plater.AddExtraIcon(\n        self, modTable.spellName, modTable.texture, 1, nil, \n        0, 0, nil, \n        false, false, modTable.spellId, nil, -1\n    )\n    \nend",
