					["Hooks"] = {
						["Leave Combat"] = "function (self, unitId, unitFrame, envTable, modTable)\n    --insert code here\n    -- Plater.StopGlow(unitFrame.healthBar, modTable.FreshMeatOptions.glowType, modTable.FreshMeatOptions.key)\n    modTable.unitGUIDS = {};\n    -- stop all glow effects or a specific one\nend\n\n\n",
						["Nameplate Updated"] = "function (self, unitId, unitFrame, envTable, modTable)\n    --insert code here      \n    if(not modTable.unitGUIDS[unitFrame.namePlateUnitGUID] and ((Plater.IsInCombat() and unitFrame.InCombat) or modTable.config.showOutOfCombat)) then        \n        unitFrame.FreshMeatUsed:Show()\n    else\n        unitFrame.FreshMeatUsed:Hide()\n    end\nend\n\n\n\n\n\n\n\n\n\n\n",
						["Destructor"] = "function (self, unitId, unitFrame, envTable, modTable)       \n    unitFrame.FreshMeatUsed:Hide()\n    unitFrame.FreshMeatUsed = nil\nend",
						["Constructor"] = "function (self, unitId, unitFrame, envTable, modTable)\n    --Get configured options and load into variables\n    local spellIdOrName = tonumber(modTable.config.spellToDetect) or modTable.config.spellToDetect\n    local spellTextureToShow = tonumber(modTable.config.spellTextureToShow) or modTable.config.spellTextureToShow    \n    envTable.Texture = GetSpellTexture(spellTextureToShow)\n    envTable.Size = modTable.config.iconSize\n    envTable.Anchor = {\n        side = 6, --right side\n        x = 0,\n        y = 0\n    }      \n    -----------------------------------------------------------------------------------\n    \n    if(not modTable.unitGUIDS)then\n        modTable.unitGUIDS = {};\n    end    \n    if (not unitFrame.FreshMeatUsed) then\n        unitFrame.FreshMeatUsed = unitFrame.healthBar:CreateTexture (nil, \"OVERLAY\", 10)\n        local texture = unitFrame.FreshMeatUsed        \n        texture:SetTexture (envTable.Texture)\n        texture:SetSize (envTable.Size, envTable.Size)\n        if(not envTable.showBorder)\n        then\n            texture:SetTexCoord (.08, .92, .08, .92)\n        end\n        Plater.SetAnchor (texture, envTable.Anchor)\n    end\n    \n    -- functions\n    local playerGUID = UnitGUID(\"player\")    \n    local function parseEvent(...)\n        --local timestamp, subevent, _, sourceGUID, sourceName, sourceFlags, sourceRaidFlags, destGUID, destName, destFlags, destRaidFlags\n        local _, subevent, _, sourceGUID, _, _, _, destGUID, _, _, _, spellID,spellName = ...\n        local targetGUID = UnitGUID(\"target\");\n        local mouseoverGUID = UnitGUID(\"mouseover\");\n        local IsSpellToDetect = spellIdOrName == spellID or spellIdOrName == spellName;   \n        local IsMyOwnSpell = subevent == \"SPELL_DAMAGE\" and sourceGUID == playerGUID     \n        \n        if IsMyOwnSpell and IsSpellToDetect then            \n            for _, plateFrame in ipairs (Plater.GetAllShownPlates()) do\n                if (plateFrame.namePlateUnitGUID == destGUID and (destGUID  == mouseoverGUID)) then\n                    modTable.unitGUIDS[destGUID]=true                    \n                elseif (plateFrame.namePlateUnitGUID == destGUID and (destGUID  == targetGUID) and (mouseoverGUID == nil)) then\n                    modTable.unitGUIDS[destGUID]=true   \n                end\n            end\n        end        \n    end  \n    local eventFrame = CreateFrame(\"Frame\")\n    eventFrame:RegisterEvent(\"COMBAT_LOG_EVENT_UNFILTERED\")\n    eventFrame:SetScript(\"OnEvent\", function(self, event, ...) \n            if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n                parseEvent(CombatLogGetCurrentEventInfo())\n            end\n    end)\n    \nend\n\n\n\n\n\n\n\n\n\n\n\n\n",
